import React, { useRef, useEffect, useState } from "react";
import "./App.css";
import "./PongBoard.css";

const BOARD_WIDTH = 400;
const BOARD_HEIGHT = 600;
const PADDLE_WIDTH = 80;
const PADDLE_HEIGHT = 12;
const BALL_SIZE = 12;
const PADDLE_SPEED = 6;
const INITIAL_BALL_SPEED = 4;
const SPEED_INCREMENT = 1.03; // 3% speed up after every paddle hit

function clamp(val, min, max) {
  return Math.max(min, Math.min(val, max));
}

const initialState = () => ({
  ball: {
    x: BOARD_WIDTH / 2 - BALL_SIZE / 2,
    y: BOARD_HEIGHT / 2 - BALL_SIZE / 2,
    vx: INITIAL_BALL_SPEED * (Math.random() > 0.5 ? 1 : -1),
    vy: INITIAL_BALL_SPEED * (Math.random() > 0.5 ? 1 : -1),
    speed: INITIAL_BALL_SPEED,
  },
  paddles: {
    top: BOARD_WIDTH / 2 - PADDLE_WIDTH / 2,
    bottom: BOARD_WIDTH / 2 - PADDLE_WIDTH / 2,
  },
  scores: {
    top: 0,
    bottom: 0,
  },
});

export default function App() {
  const [state, setState] = useState(initialState());
  const [isRunning, setIsRunning] = useState(false);
  const keys = useRef({
    bottomLeft: false, bottomRight: false, topLeft: false, topRight: false
  });

  // Start handler
  const handleStart = () => {
    setIsRunning(true);
  };

  // Reset handler
  const handleReset = () => {
    setState(initialState());
    setIsRunning(false);
  };

  // Keyboard events
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key.toLowerCase() === "a") keys.current.bottomLeft = true;
      if (e.key.toLowerCase() === "d") keys.current.bottomRight = true;
      if (e.key === "ArrowLeft") keys.current.topLeft = true;
      if (e.key === "ArrowRight") keys.current.topRight = true;
    };
    const handleKeyUp = (e) => {
      if (e.key.toLowerCase() === "a") keys.current.bottomLeft = false;
      if (e.key.toLowerCase() === "d") keys.current.bottomRight = false;
      if (e.key === "ArrowLeft") keys.current.topLeft = false;
      if (e.key === "ArrowRight") keys.current.topRight = false;
    };
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  // Game Loop
  useEffect(() => {
    if (!isRunning) return;
    let animationId;
    function gameLoop() {
      setState((prev) => {
        let {
          ball: { x, y, vx, vy, speed },
          paddles,
          scores,
        } = JSON.parse(JSON.stringify(prev));

        // Paddle movement
        if (keys.current.bottomLeft)
          paddles.bottom = clamp(paddles.bottom - PADDLE_SPEED, 0, BOARD_WIDTH - PADDLE_WIDTH);
        if (keys.current.bottomRight)
          paddles.bottom = clamp(paddles.bottom + PADDLE_SPEED, 0, BOARD_WIDTH - PADDLE_WIDTH);
        if (keys.current.topLeft)
          paddles.top = clamp(paddles.top - PADDLE_SPEED, 0, BOARD_WIDTH - PADDLE_WIDTH);
        if (keys.current.topRight)
          paddles.top = clamp(paddles.top + PADDLE_SPEED, 0, BOARD_WIDTH - PADDLE_WIDTH);

        // Ball movement
        x += vx;
        y += vy;

        // Wall collision (left/right)
        if (x < 0) {
          x = 0;
          vx = -vx;
        }
        if (x > BOARD_WIDTH - BALL_SIZE) {
          x = BOARD_WIDTH - BALL_SIZE;
          vx = -vx;
        }

        // --- Improved Paddle collision logic (bottom paddle) ---
        const bottomPaddleY = BOARD_HEIGHT - PADDLE_HEIGHT - 24;
        if (
          y + BALL_SIZE >= bottomPaddleY &&
          y <= bottomPaddleY + PADDLE_HEIGHT &&
          x + BALL_SIZE >= paddles.bottom &&
          x <= paddles.bottom + PADDLE_WIDTH &&
          vy > 0
        ) {
          const angle = Math.atan2(vy, vx);
          speed *= SPEED_INCREMENT;
          vx = Math.cos(angle) * speed;
          vy = -Math.abs(Math.sin(angle) * speed);
          y = bottomPaddleY - BALL_SIZE; // Place ball just above paddle
        }

        // --- Improved Paddle collision logic (top paddle) ---
        const topPaddleY = 8;
        if (
          y <= topPaddleY + PADDLE_HEIGHT &&
          y + BALL_SIZE >= topPaddleY &&
          x + BALL_SIZE >= paddles.top &&
          x <= paddles.top + PADDLE_WIDTH &&
          vy < 0
        ) {
          const angle = Math.atan2(vy, vx);
          speed *= SPEED_INCREMENT;
          vx = Math.cos(angle) * speed;
          vy = Math.abs(Math.sin(angle) * speed);
          y = topPaddleY + PADDLE_HEIGHT; // Place ball just below paddle
        }

        // Score (top missed)
        if (y < 0) {
          scores.bottom += 1;
          return { ...initialState(), scores };
        }
        // Score (bottom missed)
        if (y + BALL_SIZE > BOARD_HEIGHT) {
          scores.top += 1;
          return { ...initialState(), scores };
        }

        return {
          ball: { x, y, vx, vy, speed },
          paddles,
          scores,
        };
      });
      animationId = requestAnimationFrame(gameLoop);
    }
    animationId = requestAnimationFrame(gameLoop);
    return () => cancelAnimationFrame(animationId);
  }, [isRunning]);

  return (
    <div className="app-center">
      <button
        className="start-btn top-btn"
        onClick={handleStart}
        disabled={isRunning}
        style={{ marginBottom: 24 }}
      >
        Start
      </button>
      <div className="pong-board" style={{ width: BOARD_WIDTH, height: BOARD_HEIGHT }}>
        <div className="score left-score">{state.scores.top}</div>
        <div className="score right-score">{state.scores.bottom}</div>
        <div className="center-line" />
        {/* Top paddle */}
        <div
          className="paddle top-paddle"
          style={{ left: state.paddles.top, width: PADDLE_WIDTH, height: PADDLE_HEIGHT }}
        />
        {/* Bottom paddle */}
        <div
          className="paddle bottom-paddle"
          style={{
            left: state.paddles.bottom,
            width: PADDLE_WIDTH,
            height: PADDLE_HEIGHT,
            top: "unset",
            bottom: 24,
          }}
        />
        {/* Ball */}
        <div
          className="ball"
          style={{
            left: state.ball.x,
            top: state.ball.y,
            width: BALL_SIZE,
            height: BALL_SIZE,
          }}
        />
        <div className="player-label" style={{ top: 28 }}>
          Top: ← →
        </div>
        <div className="player-label" style={{ bottom: 28, top: "unset" }}>
          Bottom: A D
        </div>
      </div>
      <button className="reset-btn" onClick={handleReset}>
        Reset
      </button>
    </div>
  );
}
